def encode(string):
    sum = 0
    for i in range(len(string)):
        sum += ord(string[i])*i
    return sum
path = 'set1/Dynamic_Analysis_Data_Part1/Benign'  + '/*'
opFile = open('dataBenign.csv','w')
total = 0
for filepath in sorted(glob.iglob(path)):
    file = open(filepath,'r')
    hashName = filepath.split('/')[-1].split('\\')[-1].split('.')[0]
    opFile.write(hashName + ',')
    jsonData = json.loads(file.read())
    callCountTemp = jsonData['behavior']['apistats']
    callCount = frequencyDict
    for item in callCountTemp:
        for key in callCountTemp[item]:
            callCount[encode(key)] =0
            callCount[encode(key)] += int(callCountTemp[item][key])
    total += 1
    if(total%25 == 0):
        print(total, filepath)
    for item in frequencyDict:
        opFile.write(str(frequencyDict[item]) + ',')
    frequencyDict = dict.fromkeys(apiCallsEncoded, 0)
    opFile.write('0\n')

